import { Component } from '@angular/core';

@Component({
  selector: 'app-todo',
  templateUrl: './todo.component.html',
  styleUrls: ['./todo.component.css']
})
export class TodoComponent {
  newTask = '';
  newTaskPriority: number = 1; // Default priority is 1
  tasks: Task[] = [];

  addTask() {
    if (this.newTask.trim() !== '') {
      const newTask: Task = {
        description: this.newTask,
        priority: this.newTaskPriority

      };
      this.tasks.push(newTask);
      this.sortTasksByPriority(); // Call the sorting function
      this.newTask = '';
      this.newTaskPriority = 1; // Reset priority to default after adding a task
    }
  }

  removeTask(task: Task) {
    const index = this.tasks.indexOf(task);
    if (index !== -1) {
      this.tasks.splice(index, 1);
    }
  }

  private sortTasksByPriority() {
    this.tasks.sort((a, b) => a.priority - b.priority);
  }
}

interface Task {
  description: string;
  priority: number;
  completed: boolean;
}
<div class="container mt-5">
  <h2>To-Do List</h2>
  <div class="input-group mb-3">
    <input [(ngModel)]="newTask" class="form-control" placeholder="Add a new task..." />
    <select [(ngModel)]="newTaskPriority" class="form-control" style="max-width: 280px;">
      <option value="1" disabled selected>Priority</option>
      <option value="1">1</option>
      <option value="2">2</option>
      <option value="3">3</option>
      <option value="4">4</option>
      <option value="5">5</option>
    </select>    
    <div class="input-group-append">
      <button (click)="addTask()" class="btn btn-primary">Add</button>
    </div>
  </div>
  <ul class="list-group">
    <li *ngFor="let task of tasks" class="list-group-item">
      Priority {{ task.priority }}: {{ task.description }}
      <button (click)="removeTask(task)" class="btn btn-danger btn-sm float-right">Delete</button>
    </li>
  </ul>
</div>
