import { Component } from '@angular/core';

@Component({
  selector: 'app-todo',
  templateUrl: './todo.component.html',
  styleUrls: ['./todo.component.css']
})
export class TodoComponent {
  newTask = '';
  newTaskPriority: number = 1;
  tasks: Task[] = [];

  ngOnInit() {
    this.loadTasksFromLocalStorage();
  }

  loadTasksFromLocalStorage() {
    const storedTasks = localStorage.getItem('tasks');
    if (storedTasks) {
      this.tasks = JSON.parse(storedTasks);
    }
  }
  addTask() {
    if (this.newTask.trim() !== '') {
      const newTask: Task = {
        description: this.newTask,
        priority: this.newTaskPriority,
        completed: false
      };
      this.tasks.push(newTask);
      this.sortTasksByPriority();
      this.newTask = '';
      this.newTaskPriority = 1;
      this.saveTasksToLocalStorage();
    }
  }

  removeTask(task: Task) {
    const index = this.tasks.indexOf(task);
    if (index !== -1) {
      this.tasks.splice(index, 1);
      this.saveTasksToLocalStorage();
    }
  }

  private saveTasksToLocalStorage() {
    localStorage.setItem('tasks', JSON.stringify(this.tasks));
  }

  private sortTasksByPriority() {
    this.tasks.sort((a, b) => a.priority - b.priority);
  }
}

interface Task {
  description: string;
  priority: number;
  completed: boolean;
}
<div class="container mt-5" style="max-width: 800px;">
  <h2>To-Do Hero</h2>
  <div class="input-group mb-3">
    <input [(ngModel)]="newTask" class="form-control" placeholder="What are your tasks f..." />
    <select [(ngModel)]="newTaskPriority" class="form-control" style="max-width: 200px;">
      <option value="1" disabled selected>Priority</option>
      <option value="1">1</option>
      <option value="2">2</option>
      <option value="3">3</option>
      <option value="4">4</option>
      <option value="5">5</option>
    </select>    
    <div class="input-group-append">
      <button (click)="addTask()" class="btn btn-primary" style="margin-left: 5px;">Add</button>
    </div>
  </div>
  <ul class="list-group">
    <li *ngFor="let task of tasks" class="list-group-item">
      <input type="checkbox" [(ngModel)]="task.completed" class="mr-2"> 
      <span [ngStyle]="{'text-decoration': task.completed ? 'line-through' : 'none'}">
         {{ task.description }} : Priority {{ task.priority }}
      </span>
      <button (click)="removeTask(task)" class="btn btn-danger btn-sm float-right">Delete</button>
    </li>
  </ul>

</div>
