import { Component } from '@angular/core';

@Component({
  selector: 'app-todo',
  templateUrl: './todo.component.html',
  styleUrls: ['./todo.component.css']
})
export class TodoComponent {
  newTask = '';
  tasks: string[] = [];

  addTask() {
    if (this.newTask.trim() !== '') {
      this.tasks.push(this.newTask);
      this.newTask = '';
    }
  }

  removeTask(task: string) {
    const index = this.tasks.indexOf(task);
    if (index !== -1) {
      this.tasks.splice(index, 1);
    }
  }
}
<div class="container mt-5">
  <h2>To-Do List</h2>
  <div class="input-group mb-3">
    <input [(ngModel)]="newTask" (keyup.enter)="addTask()" class="form-control" placeholder="Add a new task..." />
    <div class="input-group-append">
      <button (click)="addTask()" class="btn btn-primary">Add</button>
    </div>
  </div>
  <ul class="list-group">
    <li *ngFor="let task of tasks" class="list-group-item">
      {{ task }}
      <button (click)="removeTask(task)" class="btn btn-danger btn-sm float-right">Delete</button>
    </li>
  </ul>
</div>
